<script>
import anime from 'animejs';

export default {
    mounted() {
        anime({
          targets: '.assistant-ctrl__item',
          left: 0,
          delay: anime.stagger(100, {from: 'last'}) // increase delay by 100ms for each elements.
        });
    },

    data() {
      return {
          collapsed: false,
      }
    },

    methods: {
        triggle() {
            if (this.collapsed) {
                    anime({
                        targets: '.assistant-ctrl__item',
                        bottom: (target, index, length) => {
                            return (length - 1 - index) * 50;
                        },
                        delay: anime.stagger(100, {from: 'last'}) // increase delay by 100ms for each elements.
                    });
            } else {
                    anime({
                        targets: '.assistant-ctrl__item',
                        bottom: 0,
                        delay: anime.stagger(100, {from: 'last'}) // increase delay by 100ms for each elements.
                    });
            }

            this.collapsed = !this.collapsed;
        },
    }
};
</script>
